update.plsql:
update %SCHEMA%.assignment a set a.date_pattern_id = (
	select c.date_pattern_id from %SCHEMA%.class_ c where c.date_pattern_id is not null and c.uniqueid = a.class_id
) where a.date_pattern_id is null

